const Header = () => {
  const history = useHistory();
  const authToken = localStorage.getItem(AUTH_TOKEN);

  return (
    <div className="flex pa1 justify-between nowrap orange">
      <div className="flex flex-fixed white">
        <div className="fw7 mr1">GIMEBA NEWS</div>
        <Link to="/" className="ml1 no-underline white">
          new
        </Link>
        <div className="fw7 mr1">|</div>
        <Link to="/top" className="fw7 mr1 no-underline white">
          top
        </Link>
        <div className="fw7 mr1">|</div>
        <Link
          to="/search"
          className="ml1 no-underline white"
        >
          search
        </Link>
        {authToken && (
          <div className="flex">
            <div className="fw7 mr1">|</div>
            <Link
              to="/create"
              className="ml1 no-underline white"
            >
              submit
            </Link>
          </div>
        )}
      </div>
      <div className="flex flex-fixed">
        {authToken ? (
          <div
            className="fw7 mr1 pointer white"
            onClick={() => {
              localStorage.removeItem(AUTH_TOKEN);
              history.push(`/`);
            }}
          >
            logout
          </div>
        ) : (
          <Link
            to="/login"
            className="ml1 no-underline white"
          >
            login
          </Link>
        )}
      </div>
    </div>
  );


};


export default Header;
